mapper.select:
select
m_photo
from member where email=#{email}

mapper.insert:
insert into member (email, id, password, m_photo, name,
loginType, verify) values (#{email},
#{id},#{password},#{m_photo},#{name}, #{loginType}, 'Y');

mapper.update:
update member set m_photo= #{m_photo}, name=#{name}
where
email=#{email}
